<?php/** * @file * Flexpaper template file. * * This template is intended to be loaded in a colorbox/lightbox/etc. iframe * dynamically from a drupal page */?><!DOCTYPE html PUBLIC "-//W3C//DTD XHTML 1.0 Transitional//EN" "http://www.w3.org/TR/xhtml1/DTD/xhtml1-transitional.dtd"><html xmlns="http://www.w3.org/1999/xhtml" lang="en" xml:lang="en">  <head>    <meta http-equiv="Content-Type" content="text/html; charset=utf-8" />    <title>FlexPaper</title>    <style type="text/css" media="screen">      html, body  { height:100%; }      body { margin:0; padding:0; overflow:auto; }      #flashContent { display:none; }      #viewerPlaceHolder { width: 100%; height: 100%; margin: auto; }    </style>  <script type="text/javascript" src="/sites/all/libraries/flexpaper/js/flexpaper_flash.js"></script></head><body>  <div id="viewerPlaceHolder">      <script type="text/javascript">         var fp = new FlexPaperViewer(              'FlexPaperViewer',              'viewerPlaceHolder', { config : {                SwfFile : escape('<?php print url("fedora/repository/{$pid}/OBJ.swf/OBJ.swf", array("absolute" => TRUE)); ?>'),                Scale : 0.6,                ZoomTransition : 'easeOut',                ZoomTime : 0.5,                ZoomInterval : 0.2,                FitPageOnLoad : true,                FitWidthOnLoad : true,                FullScreenAsMaxWindow : false,                ProgressiveLoading : true,                MinZoomSize : 0.2,                MaxZoomSize : 5,                SearchMatchAll : false,                InitViewMode : 'Portrait',                PrintPaperAsBitmap : false,                PrintEnabled : true,                PrintToolsVisible : true,                FullScreenVisible : true,                ViewModeToolsVisible : true,                ZoomToolsVisible : true,                NavToolsVisible : true,                CursorToolsVisible : true,                SearchToolsVisible : true,                localeChain: 'en_US',                scaleMode: "exactFit"              }});      </script>    </div>  </body></html>